//Load the uxjs
var Body = document.body;

function load_UXJS(JsFile)
{
    /**
     * JsFile object: need to loading Javascript file
     */
    var Head = document.head; //Get head document in HTML
    var Load = document.createElement("script"); //create a new tab: <script>
    Load.src = JsFile; // set <script>'s src is JsFile object
    Head.appendChild(Load); // add tab:<script> to the HTML document
    return true;
}
